/* begin refined-github rules */
  html.refined-github {
    --rgh-background: var(--color-canvas-default, var(--color-bg-canvas, #181818));
  }
  html.refined-github .markdown-body del {
    color: var(--color-fg-subtle, var(--color-text-tertiary, #9b9b9b));
  }
  html.refined-github body > .footer li .btn-link,
