    color: var(--color-text-disabled, #9b9b9b);
  }
  @media (-webkit-min-device-pixel-ratio: 2) {
    html.refined-github .js-issue-row [aria-label="Open draft pull request"] svg {
      stroke: var(--color-fg-muted, var(--color-text-secondary, #afafaf));
